let myChart,option;
class PictorialBarChart extends Component {
	constructor(props){
        super(props);
        this.state = {
        };
    }
    componentWillReceiveProps(nextProps) {
    	//更新echarts数据
    	option = {
    		xAxis: {
                data: this.getXAxisData(nextProps.data),
            },
            series: [{
                name: 'hill',
                data: this.getYAxisData(nextProps.data)
            }, {
                name: 'glyph',
                data: this.getYAxisGlyphData(nextProps.data)
            }]
    	}
    	myChart.setOption(option);
    }
	componentDidMount() {
        // 基于准备好的dom，初始化echarts实例
        myChart = echarts.init(document.getElementById('pictorialBarChart'));
        option = {
            grid:{
                top: 30,
                right: 20,
                bottom: 20,
                left: 20,

            },
            tooltip: {
                trigger: 'axis',
                axisPointer: {
                    type: 'none'
                },
                formatter: function (params) {
                    return params[0].name + ': ' + params[0].value;
                }
            },
            xAxis: {
                data: this.getXAxisData(this.props.data),
                axisTick: {show: false},
                axisLine: {show: false},
                axisLabel: {
                    textStyle: {
                        color: '#8dc9d7'
                    }
                }
            },
            yAxis: {
                splitLine: {show: false},
                axisTick: {show: false},
                axisLine: {show: false},
                axisLabel: {show: false}
            },
            color: ['#8dc9d7'],
            series: [{
                name: 'hill',
                type: 'pictorialBar',
                barCategoryGap: '-130%',
                // symbol: 'path://M0,10 L10,10 L5,0 L0,10 z',
                symbol: 'path://M0,10 L10,10 C5.5,10 5.5,5 5,0 C4.5,5 4.5,10 0,10 z',
                itemStyle: {
                    normal: {
                        opacity: 0.5
                    },
                    emphasis: {
                        opacity: 1
                    }
                },
                data: this.getYAxisData(this.props.data),
                z: 10
            }, {
                name: 'glyph',
                type: 'pictorialBar',
                barGap: '-100%',
                symbolPosition: 'end',
                symbolSize: [25, 20],
                symbolOffset: [0, '-120%'],
                data: this.getYAxisGlyphData(this.props.data)
            }]
        };
        // 绘制图表
        myChart.setOption(option);
    }
    getXAxisData(data){
		let temp = [];
		for (var i = 0; i < data.length; i++) {
			temp.push(data[i].name)
		}
		return temp;
    }
    getYAxisData(data){
		let temp = [];
		for (var i = 0; i < data.length; i++) {
			temp.push(data[i].value)
		}
		return temp;
    }
    getYAxisGlyphData(data){
		let temp = [];
		for (var i = 0; i < data.length; i++) {
			temp.push({
				value: data[i].value,
				symbol: pathSymbols[data[i].name]
			})
		}
		return temp;
    }
    componentWillUnmount() {
    	myChart.dispose();
    }
	render() {
        return (
            <div id="pictorialBarChart" style={{ width: '100%', height: '150px' }}></div>
        );
    }
}

export default PictorialBarChart;